<?php

namespace TopCarBundle\Repository\Cars;

use Doctrine\ORM\EntityManagerInterface;
use TopCarBundle\Entity\Brand;
use Doctrine\ORM\Mapping;

/**
 * BrandRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class BrandRepository extends \Doctrine\ORM\EntityRepository
{
    public function __construct(EntityManagerInterface $em, Mapping\ClassMetadata $metadata = null)
    {
        parent::__construct($em,
            $metadata == null ?
                new Mapping\ClassMetadata(Brand::class) :
                $metadata
        );
    }
}
